import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';
import axios from 'axios';

function App() {
  const baseUrl="http://localhost/DB/";
  const [data, setData]=useState([]);
  const [modalInsertar, setModalInsertar] = useState(false);
  const [modalEditar, setModalEditar] = useState(false);
  const [alumnoSeleccionado, setAlumnoSeleccionado]=useState({
    id: '',
    apellido: '',
    correo: ''
  });

  const handleChange = e => {
      const {name, value} = e.target;
      setAlumnoSeleccionado((prevState)=> ({
        ...prevState,
        [name]: value

      }))
      console.log(alumnoSeleccionado);


  }



   const abrirCerrarModalInsertar=()=>{
    setModalInsertar(!modalInsertar);

  }

  const abrirCerrarModalEditar=()=>{
    setModalEditar(!modalEditar);

  }

  const peticionGet=async()=>{
    await axios.get(baseUrl)
    .then(response=>{
    setData(response.data);
    })
  }


  const peticionPost=async()=>{
    var f = new FormData();
    f.append("apellido", alumnoSeleccionado.apellido);
    f.append("correo", alumnoSeleccionado.correo);
    f.append("METHOD", "POST");
    await axios.post(baseUrl, f)
    .then(response=>{
      setData(data.concat(response.data));
      abrirCerrarModalInsertar();
    }).catch(error=>{
      console.log(error);
    })
  }

  const peticionPut=async()=>{
    var f = new FormData();
    f.append("apellido", alumnoSeleccionado.apellido);
    f.append("correo", alumnoSeleccionado.correo);
    f.append("METHOD", "PUT");
    await axios.post(baseUrl, f, {params: {id: alumnoSeleccionado.id}})
    .then(response=>{
      var dataNueva= data;
      dataNueva.map(alumno=>{
        if(alumno.id===alumnoSeleccionado.id){
          alumno.nombre=alumnoSeleccionado.apellido;
          alumno.lanzamiento=alumnoSeleccionado.correo;
                  }
      });
      setData(dataNueva);
      abrirCerrarModalEditar();
    }).catch(error=>{
      console.log(error);
    })
  }





  const seleccionarAlumno=(Alumno, caso)=>{
   setAlumnoSeleccionado(Alumno);

      (caso=="Editar")&&
      abrirCerrarModalEditar()
      }


  useEffect(()=>{
    peticionGet();
  },[])




  return (
    <div style={{textAlign: 'center'}}>
   <div>
       <table className="table table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Apelldio</th>
          <th>Crorreo</th>
          <th>Accion</th>
        </tr>
      </thead>
      <tbody>
        {data.map(Alumno=>(
          <tr key={Alumno.id}>
            <td>{Alumno.id}</td>
            <td>{Alumno.apellido}</td>
            <td>{Alumno.correo}</td>
            <td>
          <button className="btn btn-primary" onClick={()=>seleccionarAlumno(Alumno,"Editar")}>Editar</button> {"  "}
          <button className="btn btn-danger" >Eliminar</button>{"  "}
          <button className="btn btn-primary" >Crear</button>

          </td>
          </tr>
        ))}


      </tbody> 
    </table>
    <button className="btn btn-success" onClick={()=>abrirCerrarModalInsertar()}>Insertar</button>      <br /><br />


    <Modal isOpen={modalInsertar}>
      <ModalHeader>Insertar datos del Alumno</ModalHeader>
      <ModalBody>
        <div className="form-group">
          <label>apellido: </label>
          <br />
          <input type="text" className="form-control" name="apellido" placeholder="apellido" onChange={handleChange}/>
          <br />
          <label>correo: </label>
          <br />
          <input type="text" className="form-control" name="correo" placeholder="correo" onChange={handleChange}/>
          <br />
          </div>
      </ModalBody>
      <ModalFooter>
        <button className="btn btn-primary" onClick={()=>peticionPost()}>Insertar</button>{"   "}
        <button className="btn btn-danger" onClick={()=>abrirCerrarModalInsertar()}>Cancelar</button>
      </ModalFooter>
    </Modal>



    <Modal isOpen={modalEditar}>
      <ModalHeader>Editar Alumno</ModalHeader>
      <ModalBody>
        <div className="form-group">
          <label>apellido: </label>
          <br />
          <input type="text" className="form-control" name="apellido" onChange={handleChange} value={alumnoSeleccionado && alumnoSeleccionado.apellido}/>
          <br />
          <label>correo: </label>
          <br />
          <input type="text" className="form-control" name="correo" onChange={handleChange} value={alumnoSeleccionado && alumnoSeleccionado.correo}/>
          <br />
          </div>
      </ModalBody>
      <ModalFooter>
        <button className="btn btn-primary" onClick={()=>peticionPut()}>Editar</button>{"   "}
        <button className="btn btn-danger" onClick={()=>abrirCerrarModalEditar()}>Cancelar</button>
      </ModalFooter>
    </Modal>


    </div>
    </div>
  );
}

export default App;
