import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';
import axios from 'axios';

function App() {
  const baseUrl="http://localhost/DB/";
  const [data, setData]=useState([]);
  const [modalInsertar, setModalInsertar] = useState(false);

   const abrirCerrarModalInsertar=()=>{
    setModalInsertar(!modalInsertar);

  }

  const peticionGet=async()=>{
    await axios.get(baseUrl)
    .then(response=>{
    setData(response.data);
    })
  }
  useEffect(()=>{
    peticionGet();
  },[])



  return (
    <div style={{textAlign: 'center'}}>
   <div>
       <table className="table table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Apelldio</th>
          <th>Crorreo</th>
          <th>Accion</th>
        </tr>
      </thead>
      <tbody>
        {data.map(Alumno=>(
          <tr key={Alumno.id}>
            <td>{Alumno.id}</td>
            <td>{Alumno.apellido}</td>
            <td>{Alumno.correo}</td>
            <td>
          <button className="btn btn-primary">Editar</button> {"  "}
          <button className="btn btn-danger" >Eliminar</button>{"  "}
          <button className="btn btn-primary" >Crear</button>

          </td>
          </tr>
        ))}


      </tbody> 
    </table>
    <button className="btn btn-#000aff" onClick={()=>abrirCerrarModalInsertar()}>Insertar</button>      <br /><br />


    <Modal isOpen={modalInsertar}>
      <ModalHeader>Insertar Framework</ModalHeader>
      <ModalBody>
        <div className="form-group">
          <label>Nombre: </label>
          <br />
          <input type="text" className="form-control"/>
          <br />
          <label>Lanzamiento: </label>
          <br />
          <input type="text" className="form-control"/>
          <br />
          <label>Desarrollador: </label>
          <br />
          <input type="text" className="form-control"/>
          <br />
        </div>
      </ModalBody>
      <ModalFooter>
        <button className="btn btn-primary" >Insertar</button>{"   "}
        <button className="btn btn-danger" onClick={()=>abrirCerrarModalInsertar()}>Cancelar</button>
      </ModalFooter>
    </Modal>


    </div>
    </div>
  );
}

export default App;
