import React, {useState, useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';
import axios from 'axios';

function App() {
  const baseUrl="http://localhost/DB/";
  const [data, setData]=useState([]);
  const [modalInsertar, setModalInsertar] = useState(false);
  const [modalEditar, setModalEditar] = useState(false);
  const [modalCredencial, setModalCredencial] = useState(false);
  const [modalEliminar, setModalEliminar] = useState(false);
  const [alumnoSeleccionado, setAlumnoSeleccionado]=useState({
    id: '',
    apellido: '',
    correo: ''
  });

  const handleChange = e => {
      const {name, value} = e.target;
      setAlumnoSeleccionado((prevState)=> ({
        ...prevState,
        [name]: value

      }))
      console.log(alumnoSeleccionado);


  }



   const abrirCerrarModalInsertar=()=>{
    setModalInsertar(!modalInsertar);

  }

  const abrirCerrarModalEditar=()=>{
    setModalEditar(!modalEditar);

    const abrirCerrarModalCredencial=()=>{
      setModalCredencial(!modalCredencial);
  }
  const abrirCerrarModalEliminar=()=>{
    setModalEliminar(!modalEliminar);

  }

  const peticionGet=async()=>{
    await axios.get(baseUrl)
    .then(response=>{
    setData(response.data);
    })
  }


  const peticionPost=async()=>{
    var f = new FormData();
    f.append("apellido", alumnoSeleccionado.apellido);
    f.append("correo", alumnoSeleccionado.correo);
    f.append("METHOD", "POST");
    await axios.post(baseUrl, f)
    .then(response=>{
      setData(data.concat(response.data));
      abrirCerrarModalInsertar();
    }).catch(error=>{
      console.log(error);
    })
  }

  const peticionPut=async()=>{
    var f = new FormData();
    f.append("apellido", alumnoSeleccionado.apellido);
    f.append("correo", alumnoSeleccionado.correo);
    f.append("METHOD", "PUT");
    await axios.post(baseUrl, f, {params: {id: alumnoSeleccionado.id}})
    .then(response=>{
      var dataNueva= data;
      dataNueva.map(alumno=>{
        if(alumno.id===alumnoSeleccionado.id){
          alumno.nombre=alumnoSeleccionado.apellido;
          alumno.lanzamiento=alumnoSeleccionado.correo;
                  }
      });
      setData(dataNueva);
      abrirCerrarModalEditar();
    }).catch(error=>{
      console.log(error);
    })
  }

  const peticionCredencial=async()=>{
    var f = new FormData();
    f.append("apellido", alumnoSeleccionado.apellido);
    f.append("correo", alumnoSeleccionado.correo);
    f.append("METHOD", "PUT");
    await axios.post(baseUrl, f, {params: {id: alumnoSeleccionado.id}})
    .then(response=>{
      var dataNueva= data;
      dataNueva.map(alumno=>{
        if(alumno.id===alumnoSeleccionado.id){
          alumno.nombre=alumnoSeleccionado.apellido;
          alumno.lanzamiento=alumnoSeleccionado.correo;
                  }
      });
      setData(dataNueva);
      abrirCerrarModalEditar();
    }).catch(error=>{
      console.log(error);
    })
  }



  const seleccionarAlumno=(Alumno, caso)=>{
   setAlumnoSeleccionado(Alumno);

      (caso=="Editar")?
      abrirCerrarModalEditar():
      abrirCerrarModalEliminar();
      abrirCerrarModalCredencial();
      }

      const peticionDelete=async()=>{
        var f = new FormData();
        f.append("METHOD", "DELETE");
        await axios.post(baseUrl, f, {params: {id: alumnoSeleccionado.id}})
        .then(response=>{
          setData(data.filter(Alumno=>Alumno.id!==alumnoSeleccionado.id));
          abrirCerrarModalEliminar();
        }).catch(error=>{
          console.log(error);
        })
      }


  useEffect(()=>{
    peticionGet();
  },[])




  return (
    <div style={{textAlign: 'center'}}>
   <div>
       <table className="table table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Apelldio</th>
          <th>Crorreo</th>
          <th>Accion</th>
        </tr>
      </thead>
      <tbody>
        {data.map(Alumno=>(
          <tr key={Alumno.id}>
            <td>{Alumno.id}</td>
            <td>{Alumno.apellido}</td>
            <td>{Alumno.correo}</td>
            <td>
          <button className="btn btn-primary" onClick={()=>seleccionarAlumno(Alumno,"Editar")}>Editar</button> {"  "}
          <button className="btn btn-danger" onClick={()=>seleccionarAlumno(Alumno,"Eliminar")}>Eliminar</button>{"  "}
          <button className="btn btn-primary" >Crear</button>

          </td>
          </tr>
        ))}


      </tbody> 
    </table>
    <button className="btn btn-success" onClick={()=>abrirCerrarModalInsertar()}>Insertar</button>      <br /><br />


    <Modal isOpen={modalInsertar}>
      <ModalHeader>Insertar datos del Alumno</ModalHeader>
